import React from 'react';
import {useState} from 'react'
import Switch from "react-switch";
import {FaPowerOff} from 'react-icons/fa';
import {useSelector, useDispatch} from 'react-redux';
import {bindActionCreators} from 'redux';
import {actionCreators } from '../state/index';


const Power = (props) => {

   const state = useSelector(state =>state.clips)
    console.log('state', state)

    const dispatch = useDispatch()
    const {clipsAction, powerOffAction} = bindActionCreators(actionCreators, dispatch)

    console.log("powerOffAction", powerOffAction(state))

    const [checked, setChecked] = useState(false);
    const handleChange = nextChecked => {
        setChecked(nextChecked);
        console.log("state", state)
    };

    let iconStyle ={
        color:'#B7F04D',
        fontSize: '1em',
        textAlign: 'center',
        transform: 'rotate(-90deg)',

    }
    return (
        <div className="power">
            <h2>Simple usage</h2>
            <label>
                <span>Switch with default style</span>
                <Switch
                    onChange={handleChange}
                    checked={checked}
                    offColor={"#FC7644"}
                    onColor={"#FC7644"}
                    offHandleColor={'#FC7644'}
                    onHandleColor={'#FC7644'}
                    height={20}
                    width={76}
                    uncheckedIcon={false}
                    checkedIcon={false}
                    uncheckedHandleIcon=<FaPowerOff style={iconStyle}/>
                    checkedHandleIcon=<FaPowerOff style={iconStyle}/>
                    className="react-switch"
                />
            </label>
            <p>
                The switch is <span>{checked ? "on" : "off"}</span>.
            </p>
        </div>
    );
};


export default Power;
