export const playSoundAction =(volume, clip, power)=>{
    return (dispatch)=>{
        dispatch({
            type: 'playSound',
            payload:function playSound(volume, clip){
                const sound  = document.getElementById(clip.keyTrigger)
                sound.currentTime =0
                sound.volume = volume/100
                power? sound.play() :sound.pause()
                return sound;
            }
        })
    }

}
export const powerAction = (event)=>{
    return (dispatch)=>{
        dispatch({
            type:'power',
            payload:event
        })
    }
};
export const volumeAction = (value)=>{
    return (dispatch)=>{
        dispatch({
            type:'volume',
            payload:value
        })
    }
}

export const displayAction = ()=>{
    return (dispatch)=>{
        dispatch({
            type:'display',
            payload:console.log("clicked")
        })
    }
}
