export const clipsAction = (clips)=>{
    return(dispatch)=>{
        dispatch({
            type: "powerOn",
            payload:clips
        })
    }
}

export const powerOffAction = (check)=>{
    return (dispatch)=>{
        dispatch({
            type:'powerOff',
            payload: check? state : []
        })
    }
}
