import React from 'react';
import {useState} from 'react'
import Switch from "react-switch";
import {FaPowerOff} from 'react-icons/fa'

const Power = (props) => {
    const [checked, setChecked] = useState(false);
    const handleChange = nextChecked => {
        setChecked(nextChecked);
    };

    let iconStyle ={
        color:'#B7F04D',
        fontSize: '1em',
        textAlign: 'center',
        transform: 'rotate(-90deg)',
        marginTop:'0.1em'
    }
    return (
        <div className="power">
            <h2>Simple usage</h2>
            <label>
                <span>Switch with default style</span>
                <Switch
                    onChange={handleChange}
                    checked={checked}
                    offColor={"#FC7644"}
                    onColor={"#FC7644"}
                    offHandleColor={'#FC7644'}
                    onHandleColor={'#FC7644'}
                    height={23}
                    width={77}
                    uncheckedIcon={false}
                    checkedIcon={false}
                    uncheckedHandleIcon=<FaPowerOff style={iconStyle}/>
                    checkedHandleIcon=<FaPowerOff style={iconStyle}/>
                    className="react-switch"
                />
            </label>
            <p>
                The switch is <span>{checked ? "on" : "off"}</span>.
            </p>
        </div>
    );
};


export default Power;
